  function Scan(name,type){
    let docs = document.getElementsByName(name);

    if(type===""Choice""){
      for(let i=0;i<docs.length;i++){
        if(docs[i].checked==true) {
          switch(i){
            case 0: return ""A"";
            case 1: return ""B"";
            case 2: return ""C"";
            case 3: return ""D"";
          }
        }
        
      }
      return """";
    }
    //如果是填空题
    else if(type===""Fill""){
      return docs[0].value; 
    }
    //如果是判断题
    else if(type===""Judge""){
      for(let i=0;i<docs.length;i++){
        if(docs[i].checked==true) {
          switch(i){
            case 0: return ""true"";
            case 1: return ""false"";
          }
        }
      
      }
      return """";
    }
  
  }


  

  function checkEmpty(result){
